import MUIDataTable from 'mui-datatables'

const columns = [
	{
		name: 'userId',
		label: 'User ID',
		options: {
			filter: false,
			sort: false
		}
	},
	{
		name: 'recency',
		label: 'Recency',
		options: {
			filter: false,
			sort: false
		}
	},
	{
		name: 'frequency',
		label: 'Frequency',
		options: {
			filter: false,
			sort: false
		}
	},
	{
		name: 'monetary',
		label: 'Monetary',
		options: {
			filter: false,
			sort: false
		}
	},
	{
		name: 'recency',
		label: 'Recency',
		options: {
			filter: false,
			sort: false
		}
	},
	{
		name: 'totalAmount',
		label: 'Total Amount',
		options: {
			sort: true
		}
	}
]

const options = {
	download: false,
	print: false
}

const UserRFM = ({ data }) => {
	return <MUIDataTable title={'User RFM'} data={data} columns={columns} options={options} />
}

export default UserRFM
